INSERT INTO `notes_1` VALUES ('77', '毕设赶不完了', 'HTML/CSS常见的三种水平居中方式', '1', '2022/3/2 20:48:54', '0', '<p>&lt;!DOCTYPE html&gt;<br></p><p>&lt;html lang=\"en\"&gt;</p><p>&lt;head&gt;</p><p>&nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;</p><p>&nbsp; &nbsp; &lt;meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\"&gt;</p><p>&nbsp; &nbsp; &lt;meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\"&gt;</p><p>&nbsp; &nbsp; &lt;title&gt;第二种居中方式&lt;/title&gt;</p><p>&nbsp; &nbsp; &lt;style&gt;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; .parent{</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; background: gray;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp; &nbsp; .child{</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; width: 200px;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; height: 200px;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; background: indigo;</p><p><span style=\"background-color: rgb(255, 255, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; display: table;</span></p><p><span style=\"background-color: rgb(255, 255, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; margin: 0 auto;</span></p><p>&nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &lt;/style&gt;</p><p>&lt;/head&gt;</p><p>&lt;body&gt;</p><p>&nbsp; &nbsp; &lt;!-- 父级标签 --&gt;</p><p>&nbsp; &nbsp; &lt;div class=\"parent\"&gt;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &lt;!-- 子级标签--&gt;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &lt;div class=\"child\"&gt;&lt;/div&gt;</p><p>&nbsp; &nbsp; &lt;/div&gt;</p><p>&lt;/body&gt;</p><p>&lt;/html&gt;</p>', 'admin', '1', '0');
INSERT INTO `notes_1` VALUES ('78', 'KannaKamuii', 'js如何获取一个object的第一个数据', '2', '2022/3/2 23:12:11', '0', '<p><span style=\"background-color: rgb(199, 203, 189); color: rgb(73, 73, 73); font-family: Arial, Helvetica, sans-serif;\">var obj = {</span></p><p style=\"margin: 10px auto; color: rgb(73, 73, 73); font-family: Arial, Helvetica, sans-serif; background-color: rgb(199, 203, 189);\">　　\"1\":\"123\",</p><p style=\"margin: 10px auto; color: rgb(73, 73, 73); font-family: Arial, Helvetica, sans-serif; background-color: rgb(199, 203, 189);\">　　\"2\":\"456\"</p><p style=\"margin: 10px auto; color: rgb(73, 73, 73); font-family: Arial, Helvetica, sans-serif; background-color: rgb(199, 203, 189);\">}</p><p style=\"margin: 10px auto; font-family: Arial, Helvetica, sans-serif;\"><span style=\"background-color: rgb(255, 255, 0);\"><span style=\"font-family: Consolas, &quot;Courier New&quot;, monospace; white-space: pre;\">console</span><span style=\"font-family: Consolas, &quot;Courier New&quot;, monospace; white-space: pre;\">.</span><span style=\"font-family: Consolas, &quot;Courier New&quot;, monospace; white-space: pre;\">log</span>( obj[Object.keys(obj)[0]] ); &nbsp; 控制台输出为 123</span></p><p style=\"margin: 10px auto; font-family: Arial, Helvetica, sans-serif;\"><span style=\"background-color: rgb(255, 255, 0);\"><span style=\"font-family: Consolas, &quot;Courier New&quot;, monospace; white-space: pre;\">console</span><span style=\"font-family: Consolas, &quot;Courier New&quot;, monospace; white-space: pre;\">.</span><span style=\"font-family: Consolas, &quot;Courier New&quot;, monospace; white-space: pre;\">log</span>( obj[Object.keys(obj)); 控制台输出为 [\"1\",\"2\"]</span></p>', 'admin', '1', '0');
